using Butterfly.HabboHotel.GameClients;using Butterfly.HabboHotel.Rooms.RoomIvokedItems;
using System.Collections.Generic;
using System.Linq;

namespace Butterfly.HabboHotel.Rooms.Chat.Commands.Cmd{    class roomkick : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            Room currentRoom = Session.GetHabbo().CurrentRoom;            Room room = ButterflyEnvironment.GetGame().GetRoomManager().GetRoom(Session.GetHabbo().CurrentRoomId);            if (room == null)                return;            string message = CommandManager.MergeParams(Params, 1);            if (Session.Antipub(message, "<CMD>"))                return;            RoomKick kick = new RoomKick(message, 0);            List<RoomUser> local_1 = new List<RoomUser>();            foreach (RoomUser item_0 in currentRoom.GetRoomUserManager().GetUserList().ToList())            {                if (item_0 != null && !item_0.IsBot && !item_0.GetClient().GetHabbo().HasFuse("fuse_no_kick") && kick.SaufId != item_0.GetClient().GetHabbo().Id)                {                    if (kick.allert.Length > 0)                        item_0.GetClient().SendNotification(kick.allert);                    local_1.Add(item_0);                }            }            foreach (RoomUser item_1 in local_1)            {                if (item_1 == null || item_1.GetClient() == null)                    continue;                currentRoom.GetRoomUserManager().RemoveUserFromRoom(item_1.GetClient(), true, false);            }        }    }}