using Butterfly.Communication.Packets.Outgoing.Structure;
using Butterfly.HabboHotel.GameClients;namespace Butterfly.HabboHotel.Rooms.Chat.Commands.Cmd{    class setcopylook : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            if (Params.Length != 2)                return;            string username = Params[1];            RoomUser roomUserByHabbo = Session.GetHabbo().CurrentRoom.GetRoomUserManager().GetRoomUserByHabbo(username);            if (roomUserByHabbo == null)                return;            Room currentRoom = roomUserByHabbo.mRoom;            GameClient clientByUsername = roomUserByHabbo.GetClient();            if (currentRoom == null)                return;            if (Session.Langue != clientByUsername.Langue)
            {
                UserRoom.SendWhisperChat(ButterflyEnvironment.GetLanguageManager().TryGetValue(string.Format("cmd.authorized.langue.user", clientByUsername.Langue), Session.Langue));
                return;
            }            clientByUsername.GetHabbo().Gender = Session.GetHabbo().Gender;            clientByUsername.GetHabbo().Look = Session.GetHabbo().Look;            if (roomUserByHabbo.transformation || roomUserByHabbo.IsSpectator)                return;            if (!clientByUsername.GetHabbo().InRoom)                return;                        clientByUsername.SendPacket(new UserChangeComposer(roomUserByHabbo, true));            currentRoom.SendPacket(new UserChangeComposer(roomUserByHabbo, false));        }    }}