using Butterfly.HabboHotel.GameClients;using Butterfly.HabboHotel.Rooms.Janken;

namespace Butterfly.HabboHotel.Rooms.Chat.Commands.Cmd{    class janken : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            if (Params.Length != 2)                return;            string Username = Params[1];            if (string.IsNullOrWhiteSpace(Username))                return;            Room room = UserRoom.mRoom;            if (room == null)                return;            RoomUser roomuser = room.GetRoomUserManager().GetRoomUserByHabbo(Username);            if (roomuser == null)            {                UserRoom.SendWhisperChat(ButterflyEnvironment.GetLanguageManager().TryGetValue("input.usernotfound", Session.Langue));                return;            }            if (roomuser.UserId == UserRoom.UserId)                return;            JankenManager Jankan = room.GetJanken();            Jankan.Duel(UserRoom, roomuser);        }    }}