using Butterfly.HabboHotel.GameClients;namespace Butterfly.HabboHotel.Rooms.Chat.Commands.Cmd{    class overridee : IChatCommand    {        public void Execute(GameClient Session, Room Room, RoomUser UserRoom, string[] Params)        {            if (UserRoom.AllowOverride)            {                UserRoom.AllowOverride = false;                UserRoom.SendWhisperChat(ButterflyEnvironment.GetLanguageManager().TryGetValue("override.disabled", Session.Langue));            }            else            {                UserRoom.AllowOverride = true;                UserRoom.SendWhisperChat(ButterflyEnvironment.GetLanguageManager().TryGetValue("override.enabled", Session.Langue));            }        }    }}